import React, { Component } from "react";
import '../styles/app.css';
import {Preloader} from './Preloader'
import FetchData from "./FetchData";
import 'bootstrap/dist/css/bootstrap.min.css';



class App extends Component {
  state = {
    url: null,
    isLoading: true
  }

  setUrlMin() {
    this.setState({
      url: 'data-min'
    });

  }

  setUrlMax() {
    this.setState({
      url: 'data'
    });
  }

  componentDidMount() {
    setTimeout(() => this.setState({ isLoading: false }), 1500);
  }

  render() {
    const { isLoading } = this.state;

    return (
      <div className="App">
      {/* <DataChange /> */}
      <div className="data-buttons">
        <button className="btn btn-outline-primary btn-lg" onClick={this.setUrlMin.bind(this)}>
          Get min
        </button>
        <button className="btn btn-outline-primary btn-lg" onClick={this.setUrlMax.bind(this)}>
          Get max
        </button>
      </div>

        <div className="table">
          <table className="table-striped">
            <thead className="thead-light">
              <tr className="table-articles">
                <td>id</td>
                <td>firstName</td>
                <td>lastName</td>
                <td>email</td>
                <td>phone</td>
              </tr>
            </thead>

            <tbody>
            {isLoading && <Preloader />}
              {this.state.url !== null && <FetchData urlType = {this.state.url}/>}           
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default App;
